import torch
import torch.nn as nn
from torch.utils.cpp_extension import load_inline

# Define the CUDA kernel for diagonal matrix multiplication
diag_matmul_cuda_source = """
#include <torch/extension.h>
#include <cuda_runtime.h>

template <typename scalar_t>
__global__ void diag_matmul_kernel(
    const scalar_t* __restrict__ diag,
    const scalar_t* __restrict__ mat,
    scalar_t* __restrict__ out,
    int n,
    int m) {

    int row = blockIdx.y * blockDim.y + threadIdx.y;
    int col = blockIdx.x * blockDim.x + threadIdx.x;

    if (row < n && col < m) {
        out[row * m + col] = diag[row] * mat[row * m + col];
    }
}

torch::Tensor diag_matmul_cuda(torch::Tensor diag, torch::Tensor mat) {
    const auto n = diag.size(0);
    const auto m = mat.size(1);

    auto output = torch::empty_like(mat);

    const dim3 threads(32, 32);
    const dim3 blocks((m + threads.x - 1) / threads.x, 
                      (n + threads.y - 1) / threads.y);

    AT_DISPATCH_FLOATING_TYPES(mat.scalar_type(), "diag_matmul_kernel", ([&] {
        diag_matmul_kernel<scalar_t><<<blocks, threads>>>(
            diag.data_ptr<scalar_t>(),
            mat.data_ptr<scalar_t>(),
            output.data_ptr<scalar_t>(),
            n,
            m
        );
    }));

    return output;
}
"""

diag_matmul_cpp_source = """
torch::Tensor diag_matmul_cuda(torch::Tensor diag, torch::Tensor mat);
"""

# Compile the CUDA kernel
diag_matmul = load_inline(
    name="diag_matmul",
    cpp_sources=diag_matmul_cpp_source,
    cuda_sources=diag_matmul_cuda_source,
    functions=["diag_matmul_cuda"],
    verbose=True
)

class ModelNew(nn.Module):
    def __init__(self):
        super(ModelNew, self).__init__()
        self.diag_matmul = diag_matmul

    def forward(self, A, B):
        # Move tensors to CUDA if they're not already
        A = A.cuda() if not A.is_cuda else A
        B = B.cuda() if not B.is_cuda else B

        return self.diag_matmul.diag_matmul_cuda(A, B)
