import torch
import torch.nn as nn
from torch.utils.cpp_extension import load_inline

# Define the custom CUDA kernel using cuBLAS for matrix multiplication
matmul_cuda_source = """
#include <torch/extension.h>
#include <cuda_runtime.h>
#include <cublas_v2.h>

torch::Tensor matmul_cuda(torch::Tensor A, torch::Tensor B) {
    // Ensure tensors are on CUDA
    TORCH_CHECK(A.device().is_cuda(), "A must be a CUDA tensor");
    TORCH_CHECK(B.device().is_cuda(), "B must be a CUDA tensor");

    // Get dimensions
    int M = A.size(0);
    int K = A.size(1);
    int N = B.size(1);

    TORCH_CHECK(B.size(0) == K, "Matrix dimensions don't match");

    // Create output tensor
    auto C = torch::empty({M, N}, A.options());

    // Get pointers to the data
    const float* A_data = A.data_ptr<float>();
    const float* B_data = B.data_ptr<float>();
    float* C_data = C.data_ptr<float>();

    // Create cuBLAS handle
    cublasHandle_t handle;
    cublasCreate(&handle);

    // Set the math mode to use Tensor Cores if available
    cublasSetMathMode(handle, CUBLAS_TENSOR_OP_MATH);

    // Perform matrix multiplication using cuBLAS
    // C = α*(A*B) + β*C, where α=1 and β=0
    const float alpha = 1.0f;
    const float beta = 0.0f;

    // Note: cuBLAS uses column-major ordering, but PyTorch uses row-major
    // So we compute B*A instead of A*B (effectively transposing the operation)
    cublasSgemm(handle, 
                CUBLAS_OP_N, CUBLAS_OP_N,
                N, M, K,
                &alpha,
                B_data, N,  // B is treated as the "A" matrix in cublasSgemm
                A_data, K,  // A is treated as the "B" matrix in cublasSgemm
                &beta,
                C_data, N);

    // Clean up
    cublasDestroy(handle);

    return C;
}
"""

matmul_cpp_source = """
torch::Tensor matmul_cuda(torch::Tensor A, torch::Tensor B);
"""

# Compile the inline CUDA code with cuBLAS linkage
matmul_extension = load_inline(
    name="matmul_cublas_extension",
    cpp_sources=matmul_cpp_source,
    cuda_sources=matmul_cuda_source,
    functions=["matmul_cuda"],
    verbose=True,
    with_cuda=True,
    extra_ldflags=["-lcublas"]  # Link against cuBLAS
)

class ModelNew(nn.Module):
    """
    Optimized model that performs matrix multiplication using cuBLAS
    """
    def __init__(self):
        super(ModelNew, self).__init__()
        self.matmul_cublas = matmul_extension

    def forward(self, A: torch.Tensor, B: torch.Tensor) -> torch.Tensor:
        """
        Performs matrix multiplication using cuBLAS.

        Args:
            A (torch.Tensor): Input matrix A of shape (N, N).
            B (torch.Tensor): Input matrix B of shape (N, N).

        Returns:
            torch.Tensor: Output matrix C of shape (N, N).
        """
        # Move tensors to GPU if they're not already there
        A_cuda = A.cuda() if not A.is_cuda else A
        B_cuda = B.cuda() if not B.is_cuda else B

        # Convert to float32 if needed
        A_cuda = A_cuda.to(torch.float32) if A_cuda.dtype != torch.float32 else A_cuda
        B_cuda = B_cuda.to(torch.float32) if B_cuda.dtype != torch.float32 else B_cuda

        return self.matmul_cublas.matmul_cuda(A_cuda, B_cuda)
