import torch
import torch.nn as nn
from torch.utils.cpp_extension import load_inline

matmul_cuda_source = """
#include <torch/extension.h>
#include <cuda_runtime.h>
#include <cublas_v2.h>

torch::Tensor matmul_cuda(torch::Tensor A, torch::Tensor B) {
    // Get dimensions
    int M = A.size(0);
    int K = A.size(1);
    int N = B.size(1);

    // Create output tensor
    auto options = torch::TensorOptions()
        .dtype(A.dtype())
        .device(A.device());
    auto C = torch::empty({M, N}, options);

    // Get pointers to data
    float* a_ptr = A.data_ptr<float>();
    float* b_ptr = B.data_ptr<float>();
    float* c_ptr = C.data_ptr<float>();

    // Create cuBLAS handle
    cublasHandle_t handle;
    cublasCreate(&handle);

    // Constants for GEMM operation
    float alpha = 1.0f;
    float beta = 0.0f;

    // Perform matrix multiplication using cuBLAS
    // Note: cuBLAS assumes column-major order, but PyTorch uses row-major order
    // So we compute B * A instead of A * B and adjust parameters accordingly
    cublasSgemm(handle, CUBLAS_OP_N, CUBLAS_OP_N, 
                N, M, K, 
                &alpha,
                b_ptr, N,
                a_ptr, K,
                &beta,
                c_ptr, N);

    // Destroy cuBLAS handle
    cublasDestroy(handle);

    return C;
}
"""

matmul_cpp_source = """
torch::Tensor matmul_cuda(torch::Tensor A, torch::Tensor B);
"""

# Compile the custom CUDA kernel
matmul_module = load_inline(
    name="matmul_cuda",
    cpp_sources=matmul_cpp_source,
    cuda_sources=matmul_cuda_source,
    functions=["matmul_cuda"],
    extra_ldflags=["-lcublas"],
    with_cuda=True,
    verbose=True
)

class ModelNew(nn.Module):
    """
    Optimized model that performs matrix multiplication using custom CUDA kernel
    """
    def __init__(self):
        super(ModelNew, self).__init__()

    def forward(self, A: torch.Tensor, B: torch.Tensor) -> torch.Tensor:
        """
        Performs matrix multiplication using a custom CUDA kernel.

        Args:
            A: Input tensor of shape (M, K).
            B: Input tensor of shape (K, N).

        Returns:
            Output tensor of shape (M, N).
        """
        # Move tensors to CUDA if they're not already there
        if not A.is_cuda:
            A = A.cuda()
        if not B.is_cuda:
            B = B.cuda()

        return matmul_module.matmul_cuda(A, B)
