import torch
import torch.nn as nn
from torch.utils.cpp_extension import load_inline

# Define the custom CUDA kernel for matrix multiplication
matmul_source = """
#include <torch/extension.h>
#include <cuda_runtime.h>
#include <cublas_v2.h>

torch::Tensor matmul_cuda(torch::Tensor A, torch::Tensor B) {
    // Get dimensions
    int M = A.size(0);
    int K = A.size(1);
    int N = B.size(1);

    // Create output tensor
    auto options = torch::TensorOptions()
        .dtype(A.dtype())
        .device(A.device());
    auto C = torch::empty({M, N}, options);

    // Get pointers to the data
    float *a_ptr = A.data_ptr<float>();
    float *b_ptr = B.data_ptr<float>();
    float *c_ptr = C.data_ptr<float>();

    // Initialize cuBLAS
    cublasHandle_t handle;
    cublasCreate(&handle);

    // Set up alpha and beta for gemm
    float alpha = 1.0f;
    float beta = 0.0f;

    // Perform matrix multiplication using cuBLAS
    // Note: cuBLAS uses column-major format, so we compute B*A instead of A*B
    cublasSgemm(handle, CUBLAS_OP_N, CUBLAS_OP_N, 
                N, M, K,
                &alpha,
                b_ptr, N,
                a_ptr, K,
                &beta,
                c_ptr, N);

    // Destroy cuBLAS handle
    cublasDestroy(handle);

    return C;
}
"""

matmul_cpp_source = """
torch::Tensor matmul_cuda(torch::Tensor A, torch::Tensor B);
"""

# Compile the inline CUDA code for matrix multiplication
matmul_op = load_inline(
    name="matmul_op",
    cpp_sources=matmul_cpp_source,
    cuda_sources=matmul_source,
    functions=["matmul_cuda"],
    verbose=True,
    with_cuda=True,
    extra_cuda_cflags=["-lcublas"]
)

class ModelNew(nn.Module):
    """
    Optimized model that performs a single matrix multiplication (C = A * B) with a large K dimension
    using a custom CUDA kernel that leverages cuBLAS for high performance.
    """
    def __init__(self):
        super(ModelNew, self).__init__()
        self.matmul_op = matmul_op

    def forward(self, A: torch.Tensor, B: torch.Tensor) -> torch.Tensor:
        """
        Performs matrix multiplication of A and B using optimized CUDA kernel.

        Args:
            A: Input tensor of shape (M, K)
            B: Input tensor of shape (K, N)

        Returns:
            Output tensor of shape (M, N)
        """
        # Move tensors to CUDA if they're not already there
        if not A.is_cuda:
            A = A.cuda()
        if not B.is_cuda:
            B = B.cuda()

        return self.matmul_op.matmul_cuda(A, B)
