import torch
import torch.nn as nn
from torch.utils.cpp_extension import load_inline

# Define the custom CUDA kernel for diagonal matrix multiplication
diag_matmul_source = """
#include <torch/extension.h>
#include <cuda_runtime.h>

template <typename scalar_t>
__global__ void diag_matmul_kernel(
    const scalar_t* __restrict__ diag,
    const scalar_t* __restrict__ matrix, 
    scalar_t* __restrict__ output, 
    int rows, int cols) {

    int row = blockIdx.y * blockDim.y + threadIdx.y;
    int col = blockIdx.x * blockDim.x + threadIdx.x;

    if (row < rows && col < cols) {
        output[row * cols + col] = diag[row] * matrix[row * cols + col];
    }
}

torch::Tensor diag_matmul_cuda(torch::Tensor diag, torch::Tensor matrix) {
    auto rows = matrix.size(0);
    auto cols = matrix.size(1);

    auto output = torch::empty_like(matrix);

    const dim3 threads(32, 32);  // Optimized thread block size
    const dim3 blocks((cols + threads.x - 1) / threads.x, 
                      (rows + threads.y - 1) / threads.y);

    AT_DISPATCH_FLOATING_TYPES(matrix.scalar_type(), "diag_matmul_kernel", ([&] {
        diag_matmul_kernel<scalar_t><<<blocks, threads>>>(
            diag.data_ptr<scalar_t>(), 
            matrix.data_ptr<scalar_t>(), 
            output.data_ptr<scalar_t>(), 
            rows, cols
        );
    }));

    return output;
}
"""

diag_matmul_cpp_source = """
torch::Tensor diag_matmul_cuda(torch::Tensor diag, torch::Tensor matrix);
"""

# Compile the inline CUDA code
diag_matmul_module = load_inline(
    name="diag_matmul",
    cpp_sources=diag_matmul_cpp_source,
    cuda_sources=diag_matmul_source,
    functions=["diag_matmul_cuda"],
    verbose=True
)

class ModelNew(nn.Module):
    """
    Optimized model that performs a matrix multiplication of a diagonal matrix with another matrix.
    C = diag(A) * B
    """
    def __init__(self):
        super(ModelNew, self).__init__()

    def forward(self, A, B):
        """
        Performs the optimized matrix multiplication.

        Args:
            A (torch.Tensor): A 1D tensor representing the diagonal of the diagonal matrix. Shape: (N,).
            B (torch.Tensor): A 2D tensor representing the second matrix. Shape: (N, M).

        Returns:
            torch.Tensor: The result of the matrix multiplication. Shape: (N, M).
        """
        # Move tensors to CUDA if they're not already there
        A_cuda = A.cuda() if not A.is_cuda else A
        B_cuda = B.cuda() if not B.is_cuda else B

        # Use our custom CUDA kernel
        return diag_matmul_module.diag_matmul_cuda(A_cuda, B_cuda)
